query GetModelPolicies($modelId: ID!) {
  model(id: $modelId) {
    id
    policies {
      name
      ... on PolicyFlag {
        enabled
        __typename
      }
      ... on PolicyOptions {
        options
        __typename
      }
      ... on PolicyValue {
        value
        __typename
      }
      ... on PolicyFeature {
        availability
        __typename
      }
      __typename
    }
    __typename
  }
}
