query GetRoomBounds($modelId: ID!) {
  model(id: $modelId) {
    floors {
      id
      layer {
        id
        __typename
      }
      edges(includeUsed: true) {
        id
        layer {
          id
          __typename
        }
        type
        vertices {
          id
          layer {
            id
            __typename
          }
          position {
            x
            y
            __typename
          }
          __typename
        }
        centerLineBias
        thickness
        openings {
          id
          width
          relativeCenter
          type
          height
          lowerElevation
          layer {
            id
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    rooms {
      id
      layer {
        id
        __typename
      }
      floor {
        id
        __typename
      }
      classifications {
        id
        confidence
        label
        __typename
      }
      boundary {
        edges {
          id
          __typename
        }
        __typename
      }
      holes {
        edges {
          id
          __typename
        }
        __typename
      }
      dimensionEstimates {
        area
        areaIndoor
        width
        depth
        height
        units
        __typename
      }
      label
      keywords
      __typename
    }
    __typename
  }
}
